import './Togler.css'
function Togler() {

    const getClass = () => {
        var name = document.getElementsByClassName('hidden')
       
        if (name[0].className === 'hidden') {
            name[0].className = "mynewclass"
            console.log('if');
        }
        else{
            name[0].className = "hidden"
            console.log('else');
        }
        // console.log(name[0].className);

    }

    return (
        <div>
            <button onClick={getClass}>Click</button>
            <div className='hidden'>
                <h1>After click</h1>
            </div>
        </div>
    )
}
export default Togler
