import { createSlice } from "@reduxjs/toolkit";

export const dataSlice = createSlice({
    name: "student Data",
    initialState: {
        heading: "Students",
        data: []
    },
    reducers: {
        AddingData: (state, newData) => {
            const clone = [...state.data, newData.payload]
            state.data = clone
        },
        RemoveData: (state, newData) => {
            state.data = newData.payload
        },
        UpdatetdState: (state, newState) => {
            state.data = newState.payload
        }
    }
})
export const { AddingData, RemoveData, UpdatetdState } = dataSlice.action
