import { useRef, useState } from "react"
import { Button, Form, InputGroup } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux";
import { incrementCount, decrementCount } from "../../ReduxPrac/counterSlice/CounterSlice";

function ShowRedux() {
    const hrefInput = useRef(null)
    const store = useSelector((state)=>{
        return state
    })
    // console.log(hrefInput);
    const dispach = useDispatch()

    const getValue = (e) => {
        console.log(hrefInput.current.value);
        // dispach(incrementCount(hrefInput.current.value))
    }



return <>
    <h1 style={{ marginTop: 20 + "px", marginBottom: 20 + "px" }}>Count : {store.count}</h1>
    <Form style={{ width: 500 + "px" }}>
        <InputGroup className="mb-3">
            <Form.Control
                placeholder="want to INC"
                aria-label="Recipient's username"
                aria-describedby="basic-addon2"
                type="number"
            // ref={hrefInput}


            />
            <Button variant="success" type="button" onClick={getValue} style={{ width: 140 + "px" }}>INC</Button>
        </InputGroup>
        <InputGroup className="mb-3">
            <Form.Control
                placeholder="want to DEC"
                aria-label="Recipient's username"
                aria-describedby="basic-addon2"
                type="number"
            />
            <Button variant="danger" type="button" style={{ width: 140 + "px" }}>DIC</Button>
        </InputGroup>
    </Form>
</>
}
export default ShowRedux
