import { useState } from 'react';
import './Companies.css';
import Detail from './Detail';
import Menus from './Menus';

const data = ['Epam', 'HCL', 'Adobe']
const details = [
    { companyname: 'Epam', para: 'EPAM Systems, Inc. is an American company that specializes' },
    { companyname: 'HCL', para: 'One of the worlds largest  manufacturers of customsoftware and consulting providers' },
    { companyname: 'Adobe', para: 'The companys headquarters is located in Newtown Pennsylvania, and its branches ar' }
]
function Companies() {
    const [count, setCount] = useState(0)
    const [flag, setFlag] = useState(false)

    const showMenu = () => {
        console.log()
        setFlag(true)
    }

    return (
        <div className='main'>
            <div>
                <Menus data={data} showDetails={setCount} />
            </div>

            <div className='detail'>
                <Detail data={details[count]} />
                {/* <Detail /> */}
            </div>

            <div>
                <button onClick={() => {
                    showMenu()
                }}>Login Shumaila</button>
                <button onClick={showMenu}>Login Shakir</button>

                {flag && (
                    <div>
                       <div> Username:'bewithshumaila'</div>
                        <div> password:'12345'</div>
                    </div>


                )
                }

            </div>
        </div>
    )
}

export default Companies;

