import { useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useDispatch } from 'react-redux';
import { AddingData } from '../centerSlcie/newSlice';

function FormSt() {
    const style = {
        width: "600px",
        margin: "30px"
    }

    const refID = useRef()
    const refName = useRef()
    const refProfile = useRef()

    const clearInput = ""

    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { id: refID.current.value, name: refName.current.value, profile: refProfile.current.value }
        if (!obj.name || !obj.profile || !obj.id) {
            alert("All input are not fill")
            return
        }
        dispach(AddingData(obj))

        refName.current.value = clearInput
        refProfile.current.value = clearInput
        refNumber.current.value = clearInput
    }
    return (
        <Form style={style}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Id</Form.Label>
                <Form.Control ref={refID} type="number" placeholder="ID" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control ref={refName} type="text" placeholder="Name" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Profile</Form.Label>
                <Form.Control ref={refProfile} type="text" placeholder="Profile" />
            </Form.Group>

            <Button variant="primary" type="submit" onClick={AddObject}>
                Submit
            </Button>
        </Form>
    );
}

export default FormSt;
