import { createSlice } from "@reduxjs/toolkit";

export const contactSlice = createSlice({
    name: "contacts",
    initialState: {
        data: [],
        status: "",
        selected: {}
    },
    reducers: {
        viewItem: () => {

        },
        fillData: (state, value) => {
            state.data = value.payload
        }
    }
})
export const { deleteItem, viewItem, fillData } = contactSlice.actions
