import { useState } from "react"
import "./Acordian.css"
const allData = [
    { id: 1, title: "A", clicked: false },
    { id: 2, title: "B", clicked: false },
    { id: 3, title: "C", clicked: false },
    { id: 4, title: "D", clicked: false },
]
function AcordianBody() {
    const [show, setShow] = useState(allData)
    const ClickBtn = (data) => {
        const mapping = show.map((item) => {
            if (data.id === item.id) {
                return { ...item, clicked: !item.clicked }
            }
            else {
                return item
            }
        })
        setShow(mapping)
    }
    const showSelected = () => {
        const findSelected = show.filter((item)=>{
            return item.clicked === true
        })
        console.log(findSelected);
    }

    return (
        < div className="outer">
            <div className="mainBox">
                {show.map((item) => {
                    return <div key={item.id} className={`IntBox ${item.clicked ? "cliced" : ""}`} onClick={() => ClickBtn(item)}>
                        <div>{item.title}</div>
                        <div>{">"}</div>
                    </div>
                })}

            </div>
            <button onClick={showSelected}>Selected Item</button>
        </div>
    )
}
export default AcordianBody
