import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";

function EditForm({ type }) {
    const params = useParams()
    const localData = useSelector((state) => {
        return state.contactStore
    })

    const [value, setValue] = useState({
        id: Math.random(),
        name: "",
        email: "",
        addres: "",
    })

    const navigate = useNavigate()

    const chengeHandller = (e) => {
        const clone = { ...value }
        clone[e.target.name] = e.target.value
        setValue(clone)
    }

    const fechingData = async () => {
        if (type.name === "Update") {
            const res = await axios.get(`http://localhost:5000/contact/${params.id}`)
            setValue(res.data)
        } else {
            return
        }
    }


    useEffect(() => {
        fechingData()
    }, [params])

    const submitData = async () => {
        if (type.name === "Update") {
            await axios.put(`http://localhost:5000/contact/${params.id}`, value)
            
        }else{
           
        }
        navigate('/contacts')
    }

    return <form className="w-50 p-3">
        <div className="mb-3">
            <label htmlFor="ID" className="form-label">ID</label>
            <input type="number" disabled name='id' className="form-control" placeholder="You can neither create nor update ID" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={chengeHandller} />
        </div>
        <div className="mb-3">
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" value={value.name} name='name' className="form-control" id="exampleInputPassword1" onChange={chengeHandller} />
        </div>
        <div className="mb-3">
            <label htmlFor="email" className="form-label">Email</label>
            <input type="text" value={value.email} name='email' className="form-control" id="exampleInputPassword1" onChange={chengeHandller} />
        </div>
        <div className="mb-3">
            <label htmlFor="addres" className="form-label">Addres</label>
            <input type="text" value={value.addres} name='addres' className="form-control" id="exampleInputPassword1" onChange={chengeHandller} />
        </div>

        <button type="button" onClick={submitData} className={`btn ${type.color}`}>{type.name}</button>
    </form>
}
export default EditForm
