import { useRef, useState } from "react"
import { Button, Form, InputGroup } from "react-bootstrap"

function FormSec({ handller }) {
    const inputRef = useRef("null")
    console.log(inputRef.current.value);
    const [dis, setDis] = useState("")
    const handllerChenge = (e) => {
        setDis(e.target.value)
    }
    

    return <InputGroup className="mb-3">
        <Form.Control aria-label="Text input" ref={inputRef} value={dis} onChange={handllerChenge} />
        <Button variant="warning" onClick={() => handller(inputRef.current.value)}  disabled={dis.length ? false : true}>Search</Button>
    </InputGroup>
}
export default FormSec
