import { createSlice } from "@reduxjs/toolkit";

export const dataSlice = createSlice({
    name: "student Data",
    initialState: {
        heading: "Students",
        data: []
    },
    reducers: {
        AddingData: (state, newData) => {
            const clone = [...state.data, newData.payload]
            state.data = clone
        },
        RemoveData:(state, newData)=>{

        }
    }
})
export const { AddingData } = dataSlice.actions
