import { createContext, useReducer } from "react";
import { mockData } from './mockData'

export const ProductContext = createContext();

const productsReducer = (state, action) => {
    // console.log(action)
    // if (action.paylode) {
    //     const updatedState = { ...state }
    //     updatedState.productData.products.push(action.paylode)
    //     console.log('updatedState', updatedState);
      
    // }
    const renderData = (targetSource, data,  action)=> {
        if(!data) {
            clonedState.push(action.payload)
        } else {
            if(action.payload) {
                targetSource.productData.selectedProducts = [...data, action.payload]
            } else {
                targetSource.productData.selectedProducts = data
            }
        }
        
    }
    switch (action.type) {
        case 'all':
            const clonedState = { ...state, productData: { ...state.productData, selectedProducts: [] } }
            clonedState.productData.selectedProducts = [...state.productData.products]
            renderData(clonedState, null,  action)
            return clonedState
        case 'telivision':
            const tvData = { ...state, productData: { ...state.productData, selectedProducts: [] } }
            const newData = tvData.productData.products.filter((p) => p.type === 'telivision')
            renderData(tvData, newData,  action)
            return tvData

        case 'mobile':
            const mobileData = { ...state, productData: { ...state.productData, selectedProducts: [] } }
            const mData = mobileData.productData.products.filter((p) => p.type === 'mobile')
            mobileData.productData.selectedProducts = mData
            // renderData(tvData, newData,  action)
            return mobileData
        case 'laptop':
            const laptopData = { ...state, productData: { ...state.productData, selectedProducts: [] } }
            const lData = laptopData.productData.products.filter((p) => p.type === 'mobile')
            renderData(laptopData, lData,  action)
           
        
        default:
            return { ...mockData }
    }
}
export const ProductProvider = (props) => {
    const [state, dispatch] = useReducer(productsReducer, mockData)
    return (
        <ProductContext.Provider value={{ state, dispatch }}>
            {props.children}
        </ProductContext.Provider>
    )
}
