import { Link, useNavigate, useParams } from "react-router-dom"

function Detail({ data }) {
    const param = useParams()
    const nevigate=useNavigate()
    const backToGallary = () => {
        nevigate('/gallary')
    }
    const findData = data.gallery.galleryData.find((item) => {
       
        return item.id === +param.id

    })
    console.log(findData)
    return (
        <div>
            <img src={findData.imgUrl} style={{ width: '400px' ,height:'400px'}}/>
            <h3>{findData.id}</h3>
            <h3>{findData.details}</h3>
           <button onClick={backToGallary}>back</button>
        </div>
    )
}
export default Detail
