import { useRef, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { AddingData } from '../centerSlcie/newSlice';

function FormSt({ btn }) {
    console.log(btn);
    const style = {
        width: "600px",
        margin: "30px"
    }
    const localStore = useSelector((state) => {
        return state
    })
    const params = useParams()
    const [storege, setStorege] = useState({
        id: "",
        name: "",
        profile: "",
    })

    const chengeHandle = (e)=>{
        const clone = {...storege}
        clone[e.target.name] = e.target.value
        setStorege(clone)
    }

    if (btn.name === "Update") {
        const newData = localStore.data.find((item) => {
            return item.id === params.id
        })
        console.log(newData);
       
    }


    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { id: refID.current.value, name: refName.current.value, profile: refProfile.current.value }
        if (!obj.name || !obj.profile || !obj.id) {
            alert("All input are not fill")
            return
        }
        dispach(AddingData(obj))

    }

    return (
        <Form style={style}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Id</Form.Label>
                <Form.Control value={storege.id} onChange={chengeHandle} type="number" name='id' placeholder="ID" />
            </Form.Group>
            <Form.Group className="mb-3"  controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control value={storege.name} type="text" name='name' placeholder="Name" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Profile</Form.Label>
                <Form.Control value={storege.profile} type="text" name='profile' placeholder="Profile" />
            </Form.Group>

            <Button variant={btn.class} type="button" onClick={AddObject}>
                {btn.name}
            </Button>
        </Form>
    );
}

export default FormSt;
