import { useEffect } from "react";
import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux"
import { getData } from "./sliceUserThunk";

function UserThunk() {
    const userData = useSelector((state) => {
        return state
    })
    console.log(userData);

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getData())
    }, [])
    return <>
        {userData.status === "pending" && <h1>Loading....</h1>}
        {userData.status === "fulfilled" && <Table striped bordered hover variant="dark">
            <thead>
                <tr>
                    <th>Id</th>
                    <th>Name </th>
                    <th>Email</th>
                    <th>Addres</th>
                </tr>
            </thead>
            <tbody>
                {userData.data.map((item)=>{
                    return <tr key={item.id}>
                        <td>{item.id}</td>
                    </tr>
                })}
            </tbody>
        </Table>}
    </>
}
export default UserThunk
