import { useEffect, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { AddingData } from '../centerSlcie/newSlice';

function FormSt({btn}) {
    console.log(btn);
    const style = {
        width: "600px",
        margin: "30px"
    }
    const localStore = useSelector((state) => {
        return state
    })
    const params = useParams()

    const refID = useRef()
    const refName = useRef()
    const refProfile = useRef()

   if (btn.name === "Update") {
    const newData = localStore.data.find((item) => {
        return item.id === params.id
    })
    console.log(newData);
    refName.current.value = newData.name
    refProfile.current.value = newData.profile
    refID.current.value = newData.id
   }


    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { id: refID.current.value, name: refName.current.value, profile: refProfile.current.value }
        if (!obj.name || !obj.profile || !obj.id) {
            alert("All input are not fill")
            return
        }
        dispach(AddingData(obj))

        refName.current.value = ""
        refProfile.current.value = ""
        refID.current.value = ""
    }

    return (
        <Form style={style}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Id</Form.Label>
                <Form.Control ref={refID} type="number" placeholder="ID" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control ref={refName} type="text" placeholder="Name" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Profile</Form.Label>
                <Form.Control ref={refProfile} type="text" placeholder="Profile" />
            </Form.Group>

            <Button variant={btn.class} type="button" onClick={AddObject}>
                {btn.name}
            </Button>
        </Form>
    );
}

export default FormSt;
