import { useRef, useState } from 'react'
import { firstName } from './Parent'
function InChild({ data ,addValue}) {
    // console.log(data.arr);
    const [value, setValue] = useState({ name: '', age: '' })

    const refName = useRef()
    const refAge = useRef()
    const submitForm = (event) => {
        event.preventDefault()
        const obj = { ...value }
        obj[refName.current.name] = refName.current.value
        obj[refAge.current.name] = refAge.current.value
        // console.log(refName.current.value);
        // console.log(refAge.current.value);
        console.log(obj);
        addValue(obj)
        
    }
    return (
        <>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>age</th>
                    </tr>
                </thead>
                <tbody>
                    {data.arr.map((item, i) => {
                        return <tr key={i}>
                            <td>{item.name}</td>
                            <td>{item.age}</td>
                        </tr>
                    })}
                </tbody>
            </table>


            <form onSubmit={submitForm}>
                <input ref={refName} name='name' placeholder='name' />
                <input ref={refAge} name='age' placeholder='age'  />
                <button>Add</button>
            </form>

           

        </>
    )
}
export default InChild
