import { useState } from 'react'
import './All.css'
import Right from './Right'
const data = {
    state: [
        { id: 1, title: 'A' },
        { id: 2, title: 'B' }
    ],
    selected: []
}


function StateData() {
    const [value, setValue] = useState(data)
    const AddData = (val) => {
        const cloneData = { ...value, selected: [...value.selected] }
        const dataval = [val]
        cloneData.selected = dataval
        setValue(cloneData)
       
    }
    // console.log(data.state);
    return (
        <div className="pracBox">
            <div>
                <ul>
                    {data.state.map((item) => {
                        // console.log(item);
                        return <li key={item.id} onClick={(item) => AddData()}>{item.title}</li>
                    })}
                </ul>
            </div>
            <div>
                <Right val={data.selected} />
            </div>
        </div>
    )
}
export default StateData
