import { createSlice } from "@reduxjs/toolkit";

export const comboSlice = createSlice({
    name: "combo Slice",
    initialState: {
        tabaleData: []
    },
    reducers: {
        addData: (state,val) => {
            const clone = [...state.tabaleData]
            clone.tabaleData.push(val.payload)
            state.tabaleData = clone
            console.log(val.payload);
        }
    }
})
export const { addData } = comboSlice.actions
