import { useEffect, useState } from "react"
import axios from 'axios'
import Post from "./Post"

let start = 0
let endpoint = 0
function Posts() {
    const [post, setPost] = useState(null)
   const [oldPosts , setOldPosts] = useState(null)

    const onchenge = () => {
       start = start + 5
       endpoint = start + 5
       const newData = oldPosts.slice(start, endpoint)
       setPost(newData)
    }

    useEffect(() => {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then((post) => {
                const allpost = post.data
                const data = allpost.slice(0, 5)
                setPost(data)
                setOldPosts(allpost)
            })
            
    }, 2000)

    return (
        <div>
            <Post data={post} onchenge={onchenge} />
        </div>
    )
}
export default Posts
