import { createSlice } from "@reduxjs/toolkit";

export const counterSlice = createSlice({
    name: "counter-slice",
    initialState: {
        count: 0,
        getObj: {
            inc: 0,
            dec: 0
        }
    },
    reducers: {
        incrementCount: (val, val2) => {
            val.count = val.count + val2.payload
            val.getObj.inc = val2.getObj.inc
        },
        decrementCount: (val, val3) => {
            val.count = val.count - val3.payload
            val.getObj.dec = val3.getObj.dec
        },
        updateGetObj: (val, numbers) => {
            // console.log(val.getObj);
            console.log(numbers);
        }
    }

})

export const { incrementCount, decrementCount, updateGetObj } = counterSlice.actions
