import { Table } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { removedData } from "../slices/sliceSection";

function TableCompo({ localData, editData }) {
    const style = {
        width: 800 + "px",
        margin: "23px",
    }
    const dispach = useDispatch()

    const removeItem = (data) => {
        const removed = localData.tabaleData.filter((item) => {
            return item.name !== data.name
        })
        dispach(removedData(removed))
    }
    console.log(localData);


    return <Table style={style} striped bordered hover variant="dark">
        <thead>
            <tr>
                <th>Name</th>
                <th>Profile</th>
                <th>Phone</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
            {localData.tabaleData.length ? localData.tabaleData.map((item, i) => {
                return <tr key={i}>
                    <td>{item.name}</td>
                    <td>{item.profile}</td>
                    <td>{item.number}</td>
                    <td>
                        <button onClick={() => {
                            removeItem(item)
                        }}>Delete</button>
                        <button onClick={() => editData(item)}>Edit</button>
                    </td>
                </tr>
            }) : <></>}


        </tbody>
    </Table>
}
export default TableCompo
