import { useContext, useState } from 'react';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { useParams } from 'react-router-dom';
import { ProductContext } from '../../../Pages/Products/productProvider';
import '../Product.css'

// const a = 'hello'
function ProductModal({ show, handleClose }) {
    const context = useContext(ProductContext)
    const param = useParams()
    const [formVal, setFormVal] = useState({
        type: '',
        id: Math.random(),
        url: '',
        price: '',
        description: ' '
    })

    const changeHandler = (e) => {
        const updatedForm = { ...formVal }
        updatedForm[e.target.name] = e.target.value;
        setFormVal(updatedForm)
    }
    const submitForm = (e) => {
        e.preventDefault()
        // context.dispatch({ type: param.type, paylode: formVal })
        setFormVal({
            type: '',
            id: Math.random(),
            url: '',
            price: '',
            description: ' '
        })

    }
    return (
        <Modal show={show} onHide={handleClose}>
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Type</Form.Label>
                    <Form.Control type="Type" name='type' placeholder="mobile , tv" onChange={changeHandler} value={formVal.type} />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Url</Form.Label>
                    <Form.Control type="text" name='url' placeholder="Img Url" onChange={changeHandler}  value={formVal.url}/>
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Price</Form.Label>
                    <Form.Control type="number" name='price' placeholder="Price" onChange={changeHandler}  value={formVal.price} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Description</Form.Label>
                    <Form.Control  type="text" name='description' placeholder="description" onChange={changeHandler}  value={formVal.description} />
                </Form.Group>

                <Button onClick={submitForm}>
                    Submit
                </Button>
            </Form>
        </Modal>
    );
}

export default ProductModal;
