import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { ProductContext } from '../../Pages/Products/productProvider'
import NoDataMessege from './NodataFound/NoDataMessege'
import Product from './Product'
import InputForm from './SearchBar/InputForm'

const Products = () => {
    const context = useContext(ProductContext)
    const params = useParams()
    const { selectedProducts } = context.state.productData;
    useEffect(() => {
        context.dispatch({ type: params.type })
    }, [params.type])



    function wrapDiv(data) {
        console.log(data);
    }
    wrapDiv(input(' Java Script '))

    return (
        <div className='col-9'>
            <InputForm dispatch={context.dispatch} />
            <div className='row'>
                {selectedProducts.map(data => {
                    return <Product key={data.id} data={data} />
                })}
                {!selectedProducts.length && (<NoDataMessege />)}
            </div>

        </div>
    )
}
export default Products
