import { configureStore, createSlice } from "@reduxjs/toolkit";

const counterSlice = createSlice({
    name: "counter-slice",
    initialState: {
        value: 0
    },
    reducers: {
        incrementeVal: (state) => {
            state.value += 1
        }
        // decrimentVal: (state) => {
        //     state.value -= 1
        // }
    }
});

export const Store = configureStore({
    reducer: counterSlice.reducer
})
