import { useState } from "react"
import "./Acordian.css"
const allData = [
    { id: 1, title: "A", clicked: false },
    { id: 2, title: "B", clicked: false },
    { id: 3, title: "C", clicked: false },
    { id: 4, title: "D", clicked: false },
]
function AcordianBody() {
    const [show, setShow] = useState(allData)
    const ClickBtn = (data) => {
        const mapping = show.map((item)=>{
            if (data.id === item.id) {
                data.clicked = !data.clicked
            }else{
                return
            }
        })
        data.clicked = !data.clicked
        setShow(allData);
    }

    return (
        < div className="outer">
            <div className="mainBox">
                {show.map((item) => {
                    return <div key={item.id} className={`IntBox ${item.clicked ? "line" : "lineHide"}`} onClick={() => ClickBtn(item)}>
                        <div>{item.title}</div>
                        <div>{">"}</div>
                    </div>
                })}

            </div>
            <button >Selected Item</button>
        </div>
    )
}
export default AcordianBody
