import { createSlice } from "@reduxjs/toolkit";

export const comboSlice = createSlice({
    name: "combo Slice",
    initialState: {
        tabaleData: []
    },
    reducers: {
        addData: (state, val) => {
            const clone = [...state.tabaleData , val.payload]
            // clone.push(val.payload)
            console.log(clone);
        }
    }
})
export const { addData } = comboSlice.actions
