import axios from "axios";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"
import { useNavigate } from "react-router-dom";
import { fillData } from "../slice/sliceContact";
import TableComp from "./TableComp";

function Contacts() {
    const localStore = useSelector((state) => {
        return state.contactStore
    })

    const navigate = useNavigate()
    const dispach = useDispatch()
    const getData = async () => {
        const response = await axios.get('http://localhost:5000/contact')
        dispach(fillData(response.data))
    }

    useEffect(() => {
        getData()
    }, [])

    const deleteItem = async (id) => {
        await axios.delete(`http://localhost:5000/contact/${id}`)
        getData()
    }
    function addingContact() {
        navigate('/addContact')
    }

    return <>
        <TableComp data={localStore.data} actions={{ deleteItem }} />
        <button type="button" onClick={addingContact}>Add Contacts</button>
    </>
}
export default Contacts
