import axios from "axios";
import { useEffect, useState } from "react";
import ButtonPage from "./ButtonPage"
import InputSec from "./InputSec"
import MainNews from "./MainNews"

function HackersNews(){
   const [News , setNews] =useState(null)
   const [loader , setloader] =useState(false)
    const getValue =async (event)=>{
        setloader(true)
       const res = await axios.get(`https://hn.algolia.com/api/v1/search?query=${event.target.value}&page=0`)
       setNews(res.data);
       setloader(false)
    }
    const ShowUi = async()=>{
        const response = await axios.get(`https://hn.algolia.com/api/v1/search?query=&page=0`)
        setNews(response.data)
    }
    useEffect(()=>{
        ShowUi()
    })
    return (
        <div style={{width:'1300px', margin:'auto'}}>
            <InputSec getValue={getValue}/>
            <ButtonPage/>
            <MainNews data={News} loader={loader}/>
        </div>         
    )
}
export default HackersNews
