import { createSlice } from "@reduxjs/toolkit";

export const comboSlice = createSlice({
    name: "combo Slice",
    initialState: {
        tabaleData: []
    },
    reducers: {
        addData: (state, val) => {
            const clone = [...state.tabaleData]
            clone.push(val.payload)
        }
    }
})
export const { addData } = comboSlice.actions
