import axios from "axios"
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { myCreateAsyncThunk } from "./sliceMain/sliceMain";

function UserComp() {
    const localthunk = useSelector((state)=>{
        console.log(state);
        return state
    })
    const [loading, setLoading] = useState(false)
    const [data, setData] = useState(localthunk)
    const [errorShow, setError] = useState("")
    const dispatch = useDispatch()
    const getData = async () => {
        try {
            setLoading(true)
            const users = await axios.get("https://jsonplaceholder.typicode.com/users")
            setData(users.data.slice(0, 4))
            setError("")
            setLoading(false)
        }
        catch (error) {
            // setLoading(false)
            setData([])
            setError(error)
        }
    }
    useEffect(() => {
        // getData()
        dispatch(myCreateAsyncThunk())
    }, [])
    console.log(data);
    return <div>

        {loading && <h1>Loading.....</h1>}

        {data.length && <table className="table">
            <thead>
                <tr>
                    <th scope="col">Id</th>
                    <th scope="col">Name</th>
                    <th scope="col">UserName</th>
                    <th scope="col">Email</th>
                    <th scope="col">Address</th>
                </tr>
            </thead>
            <tbody>
                {data.data.map((item) => {
                    return <tr key={item.id}>
                        <th scope="row">{item.id}</th>
                        <td>{item.name}</td>
                        <td>{item.username}</td>
                        <td>{item.email}</td>
                        <td>{item.address.street}</td>
                    </tr>
                })}

            </tbody>
        </table>}

        {errorShow && <h2>{errorShow.message}</h2>}
    </div>
}
export default UserComp
